<section id="hero" class="py-5">
    <div class="container">
        <div class="row">
            <div class="col-12 col-md-6 align-self-md-center text-center text-md-left">
                <div class="h1 text-white text-uppercase font-weight-extra-bold">Lorem ipsum dolor</div>
                <div class="h3 text-white font-weight-normal">Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat</div>
            </div>
            <div class="col-12 col-md-6 col-lg-5 offset-lg-1">			
                <div class="p-5 bg-white rounded box-shadow-spread-white--2 mt-5 mt-md-0">
                    <form>
                        <div class="row">
                            <div class="col-12">
                                <div class="form-group mb-4">
                                    <label for="name" class="text-secondary">Nome</label>
                                    <input type="text" name="name" id="name" class="form-control form-control-lg" placeholder="Insira o nome completo">
                                </div>
                            </div>
                            <div class="col-12">
                                <div class="form-group mb-4">
                                    <label for="email" class="text-secondary">E-mail</label>
                                    <input type="email" name="email" id="email" class="form-control form-control-lg" placeholder="Insira um e-mail válido">
                                </div>
                            </div>
                            <div class="col-12">
                                <div class="form-group mb-4">
                                    <label for="password" class="text-secondary">Senha</label>
                                    <input type="password" name="password" id="password" class="form-control form-control-lg">
                                </div>
                            </div>
                            <div class="col-12">
                                <div class="form-group">
                                    <div class="form-check">
                                        <input type="checkbox" name="terms_check" id="terms_check" class="form-check-input">
                                        <label class="form-check-label small text-secondary" for="terms_check">
                                            Clicando em <strong>Cadastrar</strong>, você concorda com nossos <a role="button">Termos e Serviços</a>
                                        </label>
                                    </div>
                                </div>
                            </div>
                            <div class="col-12">
                                <button type="submit" class="btn btn-lg btn-pal-2 text-uppercase small py-4 px-5 w-100">Cadastrar</button>
                            </div>
                        </div><!-- /.row !-->
                    </form>
                </div>
            </div>
        </div>
    </div>
</section><!-- /#hero !-->
